package com.hsy.fresco.frescodemo;

import android.content.Context;
import android.net.Uri;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.DefaultItemAnimator;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.facebook.drawee.generic.GenericDraweeHierarchy;
import com.facebook.drawee.generic.RoundingParams;
import com.facebook.drawee.view.SimpleDraweeView;

public class MainActivity extends AppCompatActivity {
    public String[] imgArr = {"http://img05.tooopen.com/images/20141231/sy_78292461985.jpg",
            "http://img02.tooopen.com/images/20141231/sy_78327074576.jpg", "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTHWUGyh89Lk8WEpL_O4XovRds-HKujSLV5hczfQEyp-vklz3im",
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMjRaw9-uefrwHkYlNidBBaAgWF8r0PCfNhPz3lRz-P5EwQ8JI"};

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        RecyclerView recyclerView = (RecyclerView) findViewById(R.id.recyle);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
        // 设置ItemAnimator
        recyclerView.setItemAnimator(new DefaultItemAnimator());
        recyclerView.setAdapter(new MyAdter(this, imgArr));


    }

    static public class MyAdter extends RecyclerView.Adapter<MyAdter.ImageViewHolder> {
        Context context;
        OnItemClickListener listener;
        String[] imgArr;

        public MyAdter(MainActivity context, String[] imgArr) {
            this.context = context;
            this.imgArr = imgArr;
        }

        //设置监听事件
        public void setOnItemClickListener(OnItemClickListener listener) {
            this.listener = listener;
        }

        public MyAdter(Context context) {
            this.context = context;
        }

        /*接口点击*/
        public interface OnItemClickListener {

            void onItemClick(View view, int position);

        }

        class ImageViewHolder extends RecyclerView.ViewHolder {
            public SimpleDraweeView simpleDraweeView;
            public int position;

            public ImageViewHolder(View itemView) {
                super(itemView);
                simpleDraweeView = (SimpleDraweeView) itemView.findViewById(R.id.my_image_view);
//                //设置圆角
//                GenericDraweeHierarchy hierarchy = simpleDraweeView.getHierarchy();
//                RoundingParams roundingParams = hierarchy.getRoundingParams();
//                roundingParams.setCornersRadius(10);
//                hierarchy.setRoundingParams(roundingParams);
                //绑定点击事件
                itemView.setOnClickListener(new View.OnClickListener() {
                    @Override
                    public void onClick(View v) {
                        listener.onItemClick(v, position);
                    }
                });
            }
        }


        @Override
        public ImageViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {
            // 给ViewHolder设置布局文件
            View v = LayoutInflater.from(context).inflate(R.layout.layout, parent, false);
            return new ImageViewHolder(v);
        }

        @Override
        public void onBindViewHolder(ImageViewHolder holder, int position) {
            //绑定数据
            Uri uri = Uri.parse(imgArr[position % (imgArr.length)]);
            holder.simpleDraweeView.setImageURI(uri);
            holder.position = position;

        }


        @Override
        public int getItemCount() {
            return 100000;
        }
    }
}
